  CREATE OR REPLACE EDITIONABLE FUNCTION "HHA_PROD"."ZATCA_QR" (QR varchar2) RETURN Varchar2 IS
  l_clob         CLOB;
BEGIN

IF QR is null then
   return null;
else
l_clob := '<cac:AdditionalDocumentReference>
        <cbc:ID>QR</cbc:ID>
        <cac:Attachment>
            <cbc:EmbeddedDocumentBinaryObject mimeCode="text/plain">'||QR||'</cbc:EmbeddedDocumentBinaryObject>
        </cac:Attachment>
</cac:AdditionalDocumentReference><cac:Signature>
      <cbc:ID>urn:oasis:names:specification:ubl:signature:Invoice</cbc:ID>
      <cbc:SignatureMethod>urn:oasis:names:specification:ubl:dsig:enveloped:xades</cbc:SignatureMethod>
</cac:Signature>';  
  RETURN l_clob;
 End if; 

END;
/
