n0:createOracleTrigger.n0:source:
  CREATE OR REPLACE EDITIONABLE TRIGGER "HHA_PROD"."EBA_SB_USERS_BIU" 
    before insert or update on eba_sb_users
    for each row
begin
    if inserting then
        if :new.id is null then
            :new.id := eba_sb.gen_id();
        end if;
        :new.created_by         := nvl(v('APP_USER'), user);
        :new.created            := current_timestamp;
        :new.row_version        := 1;
        if :new.account_locked is null then
            :new.account_locked := 'N';    
        end if;
    elsif updating then
        :new.row_version        := nvl(:old.row_version,1) + 1;                                   
    end if;
    :new.updated_by         := nvl(v('APP_USER'), user);
    :new.updated            := current_timestamp;
    -- Always store username as upper case
    :new.username := upper(:new.username);
end;

/
/
ALTER TRIGGER "HHA_PROD"."EBA_SB_USERS_BIU" ENABLE;
